import React, { useEffect, useState } from 'react'
import axios from 'axios'

function Subscriber(props){
    const userTo = props.userTo
    const userFrom = props.userFrom

    const [SubscribeNumber, setSubscribeNumber] = useState(0)
    const [Subscribed, setSubscribed] = useState(false)

    const onSubscribe = () => {

        let subscribeVariabels = {
            userTo: userTo,
            userFrom: userFrom
        }

        if(Subscribed){

        }else{
            axios.post('/api/subscribe/subscribe',)            
        }

    }

    useEffect(() => {
        const subscribeNumberVariables = { userTo: userTo, userFrom: userFrom}
        axios.post('/api/subscribe/subscribeNumber', subscribeNumberVariables)
        .then(response => {
            if(response.data.success){
                setSubscribeNumber(response.data.subscribeNumber)
            }else{
                alert('Failed to get subscriber number')
            }
        })

        axios.post('/api/subscribe/subscribed', subscribeNumberVariables)
        .then(response => {
            if(response.data.success){
                console.log(response.data.subscribed)
            }else{
                alert('Failed to get Subscribed Informations')
            }
        })

    },[])


    return (
        <div>
            <button onClick={onSubscribe} style={{ backgroundColor: `${Subscribed ? '#AAAAA' : '#CC0000'}`, borderRadius: '4px', color: 'white', 'padding': '10px 16px',
             fontWeight: '500', fontSize: '1rem', textTransform:'uppercase'}}>
                {SubscribeNumber} {Subscribed? 'Subscribed':
                'Subscribe'}
            </button>
        </div>
    )
}

export default Subscriber
