import React from 'react'
import SingleComment from './SingleComment'
function ReplyComment(props){

    let renderReplyComment = () => {
        props.CommentLists && props.CommentLists.map((comment,index) => (

            <React.Fragment>
                <div style={{ marginLeft:'50px', width:'80%'}}>
            <SingleComment comment={comment} postId={props.postId} refreshFunction={props.refreshFunction} />
            <ReplyComment CommentLists={props.CommentLists} postId={props.postId} refreshFunction={props.refreshFunction} />
                </div>
            </React.Fragment>

            )


        )
    }

    return(
        <div>
            <p style={{ fontSize:'14px', margin:0, color:'gray'}} onClick>
                View more comment(s)
            </p>

           {renderReplyComment}

        </div>
    )
}

export default ReplyComment
